INSERT INTO qna (name, title, content, regdate, parent_post_id, user_id, created_at, updated_at) VALUES
('김철수', '축구 경기 일정 문의드립니다', '다음 달 경기 일정이 어떻게 되나요? 특히 주말 경기가 궁금합니다.', '2024-01-15 10:30:00', NULL, 1, '2024-01-15 10:30:00', '2024-01-15 10:30:00'),
('이영희', '티켓 예매 방법 알려주세요', '온라인으로 티켓을 예매하고 싶은데 어떻게 해야 하나요?', '2024-01-16 14:20:00', NULL, 2, '2024-01-16 14:20:00', '2024-01-16 14:20:00'),
('박민수', '경기장 주차 관련 문의', '경기장에 주차할 수 있나요? 주차비는 얼마인가요?', '2024-01-17 09:15:00', NULL, 3, '2024-01-17 09:15:00', '2024-01-17 09:15:00'),
('최지영', 'VIP 좌석 예매 가능한가요?', 'VIP 좌석 예매가 가능한지, 그리고 가격이 궁금합니다.', '2024-01-18 16:45:00', NULL, 4, '2024-01-18 16:45:00', '2024-01-18 16:45:00'),
('정수민', '경기 취소 시 환불 정책', '경기가 취소되면 티켓 환불은 어떻게 되나요?', '2024-01-19 11:30:00', NULL, 5, '2024-01-19 11:30:00', '2024-01-19 11:30:00'),
('한동훈', '팀 선수 정보 문의', '현재 팀 선수들의 정보를 어디서 확인할 수 있나요?', '2024-01-20 13:20:00', NULL, 6, '2024-01-20 13:20:00', '2024-01-20 13:20:00'),
('송미영', '경기장 내 음식 반입 가능한가요?', '경기장에 음식을 가져갈 수 있나요? 규정이 궁금합니다.', '2024-01-21 15:10:00', NULL, 7, '2024-01-21 15:10:00', '2024-01-21 15:10:00'),
('강현우', '시즌권 구매 문의', '시즌권 구매는 언제부터 가능한가요? 할인 혜택도 있나요?', '2024-01-22 10:45:00', NULL, 8, '2024-01-22 10:45:00', '2024-01-22 10:45:00'),
('윤서연', '경기장 내 사진 촬영 규정', '경기장에서 사진을 찍을 수 있나요? 규정이 궁금합니다.', '2024-01-23 14:30:00', NULL, 9, '2024-01-23 14:30:00', '2024-01-23 14:30:00'),
('임재현', '원정 경기 일정 문의', '원정 경기 일정과 티켓 예매는 어떻게 되나요?', '2024-01-24 12:15:00', NULL, 10, '2024-01-24 12:15:00', '2024-01-24 12:15:00'),
('조은영', '경기장 내 화장실 위치', '경기장 내 화장실 위치를 알려주세요.', '2024-01-25 16:20:00', NULL, 11, '2024-01-25 16:20:00', '2024-01-25 16:20:00'),
('백준호', '티켓 양도 가능한가요?', '구매한 티켓을 다른 사람에게 양도할 수 있나요?', '2024-01-26 09:40:00', NULL, 12, '2024-01-26 09:40:00', '2024-01-26 09:40:00'),
('신혜진', '경기장 내 의료실 위치', '경기장 내 의료실이 어디에 있나요? 응급상황 시 이용 가능한가요?', '2024-01-27 11:25:00', NULL, 13, '2024-01-27 11:25:00', '2024-01-27 11:25:00'),
('오태현', '경기장 내 편의시설 문의', '경기장 내 편의시설(매점, 카페 등)이 어디에 있나요?', '2024-01-28 13:50:00', NULL, 14, '2024-01-28 13:50:00', '2024-01-28 13:50:00'),
('유미라', '경기장 내 흡연 구역', '경기장 내 흡연 구역이 있나요? 위치를 알려주세요.', '2024-01-29 15:35:00', NULL, 15, '2024-01-29 15:35:00', '2024-01-29 15:35:00');
INSERT INTO qna (name, title, content, regdate, parent_post_id, user_id, created_at, updated_at) VALUES
('관리자', '축구 경기 일정 문의드립니다 - 답변', '안녕하세요. 다음 달 경기 일정은 홈페이지 일정표에서 확인하실 수 있습니다. 주말 경기는 매주 토요일 오후 3시에 진행됩니다.', '2024-01-15 11:00:00', 1, 999, '2024-01-15 11:00:00', '2024-01-15 11:00:00'),
('관리자', '티켓 예매 방법 알려주세요 - 답변', '온라인 티켓 예매는 홈페이지 티켓 예매 메뉴에서 가능합니다. 회원가입 후 로그인하여 예매하실 수 있습니다.', '2024-01-16 15:00:00', 2, 999, '2024-01-16 15:00:00', '2024-01-16 15:00:00'),
('관리자', '경기장 주차 관련 문의 - 답변', '경기장 주차는 가능하며, 주차비는 1시간당 3,000원입니다. 경기 시작 2시간 전부터 주차가 가능합니다.', '2024-01-17 10:00:00', 3, 999, '2024-01-17 10:00:00', '2024-01-17 10:00:00'),
('관리자', 'VIP 좌석 예매 가능한가요? - 답변', 'VIP 좌석 예매는 가능합니다. 가격은 50,000원이며, 홈페이지 VIP 예매 메뉴에서 구매하실 수 있습니다.', '2024-01-18 17:00:00', 4, 999, '2024-01-18 17:00:00', '2024-01-18 17:00:00'),
('관리자', '경기 취소 시 환불 정책 - 답변', '경기 취소 시 티켓은 전액 환불됩니다. 환불은 구매처를 통해 신청하실 수 있으며, 3-5일 내 처리됩니다.', '2024-01-19 12:00:00', 5, 999, '2024-01-19 12:00:00', '2024-01-19 12:00:00');
